import React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Loader2, Check } from 'lucide-react';
import { Button } from '@/components/ui/button'; // Assuming you have your shadcn button

const MorphButton = ({ 
  children, 
  onClick, 
  isLoading, 
  isSuccess, 
  className, 
  variant = "default",
  ...props 
}) => {
  return (
    <Button
      asChild
      variant={variant}
      className={`${className} relative overflow-hidden transition-all duration-300`}
      onClick={onClick}
      disabled={isLoading || isSuccess}
      {...props}
    >
      <motion.div
        layout
        initial={false}
        animate={{
          width: isSuccess ? "auto" : "100%", // Optional: shrink width on success
          backgroundColor: isSuccess ? "#22c55e" : undefined, // Turn green on success
        }}
        className="flex items-center justify-center min-w-[100px]"
      >
        <AnimatePresence mode="wait">
          {isLoading ? (
            <motion.div
              key="loading"
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
            >
              <Loader2 className="w-5 h-5 animate-spin" />
            </motion.div>
          ) : isSuccess ? (
            <motion.div
              key="success"
              initial={{ scale: 0.5, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              className="flex items-center gap-2 font-bold text-white"
            >
              <Check className="w-5 h-5" />
              <span>Done!</span>
            </motion.div>
          ) : (
            <motion.div
              key="idle"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="flex items-center gap-2"
            >
              {children}
            </motion.div>
          )}
        </AnimatePresence>
      </motion.div>
    </Button>
  );
};

export default MorphButton;
