import React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { CheckCircle2, AlertCircle, Info, Loader2, X } from 'lucide-react';
import { useNotification } from '@/contexts/NotificationContext';

const DynamicIsland = () => {
  const { activeNotification, dismiss } = useNotification();

  if (!activeNotification) return null;

  const { type, title, message } = activeNotification;

  // Configuration for different types
  const config = {
    success: { bg: 'bg-slate-900 dark:bg-white', text: 'text-white dark:text-slate-900', icon: <CheckCircle2 className="w-5 h-5 text-green-500" /> },
    error: { bg: 'bg-red-600', text: 'text-white', icon: <AlertCircle className="w-5 h-5 text-white" /> },
    loading: { bg: 'bg-slate-900 dark:bg-white', text: 'text-white dark:text-slate-900', icon: <Loader2 className="w-5 h-5 animate-spin text-violet-500" /> },
    info: { bg: 'bg-white dark:bg-slate-800 border border-slate-200 dark:border-slate-700', text: 'text-slate-900 dark:text-white', icon: <Info className="w-5 h-5 text-blue-500" /> },
  };

  const style = config[type] || config.info;

  return (
    <div className="fixed top-6 left-0 right-0 flex justify-center pointer-events-none z-[100]">
      <AnimatePresence mode="wait">
        <motion.div
          key={activeNotification.id}
          initial={{ y: -100, opacity: 0, scale: 0.9 }}
          animate={{ y: 0, opacity: 1, scale: 1 }}
          exit={{ y: -20, opacity: 0, scale: 0.95 }}
          transition={{ type: "spring", stiffness: 400, damping: 25 }}
          className={`pointer-events-auto flex items-center gap-4 px-5 py-3 rounded-full shadow-2xl ${style.bg} backdrop-blur-md`}
        >
          {/* Icon Container */}
          <div className="flex-shrink-0">
            {style.icon}
          </div>

          {/* Text Content */}
          <div className="flex flex-col min-w-[150px] max-w-[300px]">
            <span className={`text-sm font-semibold leading-tight ${style.text}`}>{title}</span>
            {message && <span className={`text-xs opacity-80 ${style.text}`}>{message}</span>}
          </div>

          {/* Close Button (Optional, mostly for sticky errors) */}
          {type !== 'loading' && (
            <button onClick={dismiss} className={`ml-2 p-1 rounded-full hover:bg-black/10 ${style.text}`}>
              <X className="w-4 h-4" />
            </button>
          )}
        </motion.div>
      </AnimatePresence>
    </div>
  );
};

export default DynamicIsland;
