import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card } from '@/components/ui/card';
import { Check, X, Loader2, Lock, Mail, ArrowRight, ChevronRight, Send, Search } from 'lucide-react';

const CreativeDemos = () => {
  return (
    <div className="min-h-screen bg-slate-50 dark:bg-slate-950 p-8 space-y-16 pb-32">
      <div className="max-w-4xl mx-auto text-center">
        <h1 className="text-4xl font-bold bg-gradient-to-r from-violet-600 to-indigo-600 bg-clip-text text-transparent mb-4">
          Creative UI Patterns
        </h1>
        <p className="text-slate-500 text-lg">
          2 distinct variations for every effect.
        </p>
      </div>

      {/* --- SECTION 1: 3D CARD FLIP --- */}
      <DemoSection title="1. The 3D Card Flip" description="Best for: Login Success / Task Completion">
        <FlipDemoA />
        <FlipDemoB />
      </DemoSection>

      {/* --- SECTION 2: SHAKE & GLOW --- */}
      <DemoSection title="2. Shake & Error" description="Best for: Validation Errors / Wrong Password">
        <ShakeDemoA />
        <ShakeDemoB />
      </DemoSection>

      {/* --- SECTION 3: BUTTON TAKEOVER --- */}
      <DemoSection title="3. Button Takeover" description="Best for: Submit Actions / Micro-interactions">
        <ButtonTakeoverA />
        <ButtonTakeoverB />
      </DemoSection>

      {/* --- SECTION 4: SPOTLIGHT FOCUS --- */}
      <DemoSection title="4. Spotlight Focus" description="Best for: Complex Forms / Guiding Attention">
        <SpotlightDemoA />
        <SpotlightDemoB />
      </DemoSection>

       {/* --- SECTION 5: CURTAIN REVEAL --- */}
       <DemoSection title="5. Curtain Reveal" description="Best for: Page Transitions (Login -> Dashboard)">
        <CurtainDemoA />
        <CurtainDemoB />
      </DemoSection>
    </div>
  );
};

/* =========================================================================
   HELPER COMPONENTS
   ========================================================================= */
const DemoSection = ({ title, description, children }) => (
  <div className="max-w-6xl mx-auto">
    <div className="mb-6 border-l-4 border-violet-500 pl-4">
      <h2 className="text-2xl font-bold text-slate-900 dark:text-white">{title}</h2>
      <p className="text-slate-500">{description}</p>
    </div>
    <div className="grid md:grid-cols-2 gap-8">
      {children}
    </div>
  </div>
);

const DemoBox = ({ title, children }) => (
  <div className="bg-white dark:bg-slate-900 border border-slate-200 dark:border-slate-800 rounded-xl p-8 flex flex-col items-center justify-center min-h-[300px] shadow-sm relative overflow-hidden">
    <h3 className="absolute top-4 left-4 text-xs font-bold uppercase tracking-wider text-slate-400">
      {title}
    </h3>
    {children}
  </div>
);

/* =========================================================================
   1. 3D FLIP DEMOS
   ========================================================================= */

// VARIATION A: Standard Horizontal Flip
const FlipDemoA = () => {
  const [isFlipped, setIsFlipped] = useState(false);
  return (
    <DemoBox title="Variation A: Standard Axis">
      <div className="perspective-1000 w-64 h-40 relative group">
        <motion.div
          className="w-full h-full relative"
          initial={false}
          animate={{ rotateY: isFlipped ? 180 : 0 }}
          transition={{ duration: 0.6, type: "spring" }}
          style={{ transformStyle: "preserve-3d" }}
        >
          {/* Front */}
          <div 
            className="absolute inset-0 bg-white border border-violet-200 shadow-xl rounded-xl p-4 flex flex-col items-center justify-center backface-hidden"
            style={{ backfaceVisibility: 'hidden' }}
          >
            <p className="font-semibold mb-2">Login</p>
            <Button size="sm" onClick={() => setIsFlipped(true)}>Sign In</Button>
          </div>
          {/* Back */}
          <div 
            className="absolute inset-0 bg-violet-600 text-white rounded-xl flex flex-col items-center justify-center backface-hidden"
            style={{ backfaceVisibility: 'hidden', transform: "rotateY(180deg)" }}
          >
            <Check className="w-8 h-8 mb-2" />
            <p className="font-bold">Welcome!</p>
            <button onClick={() => setIsFlipped(false)} className="text-xs mt-2 underline opacity-80">Reset</button>
          </div>
        </motion.div>
      </div>
    </DemoBox>
  );
};

// VARIATION B: Vertical Elastic "Ticket" Flip
const FlipDemoB = () => {
  const [isFlipped, setIsFlipped] = useState(false);
  return (
    <DemoBox title="Variation B: Vertical Elastic">
      <div className="perspective-1000 w-64 h-40 relative">
        <motion.div
          className="w-full h-full relative"
          initial={false}
          animate={{ rotateX: isFlipped ? 180 : 0 }}
          transition={{ type: "spring", stiffness: 200, damping: 14 }}
          style={{ transformStyle: "preserve-3d" }}
        >
          {/* Front */}
          <div 
            className="absolute inset-0 bg-slate-900 text-white shadow-xl rounded-xl p-4 flex flex-col items-center justify-center"
            style={{ backfaceVisibility: 'hidden' }}
          >
            <p className="font-semibold mb-2">Confirm</p>
            <Button variant="secondary" size="sm" onClick={() => setIsFlipped(true)}>Purchase</Button>
          </div>
          {/* Back */}
          <div 
            className="absolute inset-0 bg-green-500 text-white rounded-xl flex flex-col items-center justify-center"
            style={{ backfaceVisibility: 'hidden', transform: "rotateX(180deg)" }}
          >
            <p className="text-3xl font-black tracking-widest">PAID</p>
            <button onClick={() => setIsFlipped(false)} className="text-xs mt-2 underline opacity-80">Reset</button>
          </div>
        </motion.div>
      </div>
    </DemoBox>
  );
};

/* =========================================================================
   2. SHAKE & GLOW DEMOS
   ========================================================================= */

// VARIATION A: Classic "Head Shake"
const ShakeDemoA = () => {
  const [shake, setShake] = useState(0);
  return (
    <DemoBox title="Variation A: The 'Head Shake'">
      <motion.div
        key={shake}
        initial={{ x: 0 }}
        animate={{ x: shake ? [0, -10, 10, -10, 10, 0] : 0 }}
        transition={{ duration: 0.4 }}
        className="w-64"
      >
        <Card className={`p-4 border transition-colors duration-300 ${shake ? 'border-red-500 bg-red-50' : ''}`}>
          <Input placeholder="Enter Password" className={shake ? 'border-red-500' : ''} />
          <Button 
            className="w-full mt-2 bg-slate-900" 
            onClick={() => setShake(s => s + 1)}
          >
            Submit (Fail)
          </Button>
        </Card>
      </motion.div>
    </DemoBox>
  );
};

// VARIATION B: "Glitch" & Blur Shake
const ShakeDemoB = () => {
  const [shake, setShake] = useState(0);
  return (
    <DemoBox title="Variation B: Glitch & Blur">
      <motion.div
        key={shake}
        animate={shake ? { 
          x: [0, -5, 5, -5, 5, 0],
          filter: ["blur(0px)", "blur(2px)", "blur(0px)"],
          scale: [1, 0.98, 1]
        } : {}}
        transition={{ duration: 0.3 }}
        className="w-64 relative"
      >
        <Card className="p-4 overflow-hidden relative">
          {/* Flash Overlay */}
          <motion.div 
            animate={shake ? { opacity: [0, 0.5, 0] } : { opacity: 0 }}
            className="absolute inset-0 bg-red-500 pointer-events-none z-10"
          />
          <Input placeholder="Username" />
          <Button 
            className="w-full mt-2 bg-red-600 hover:bg-red-700" 
            onClick={() => setShake(s => s + 1)}
          >
            Delete (Fail)
          </Button>
        </Card>
      </motion.div>
    </DemoBox>
  );
};

/* =========================================================================
   3. BUTTON TAKEOVER DEMOS
   ========================================================================= */

// VARIATION A: Width Expansion
const ButtonTakeoverA = () => {
  const [status, setStatus] = useState('idle'); // idle -> loading -> success

  const handleClick = () => {
    setStatus('loading');
    setTimeout(() => {
      setStatus('success');
      setTimeout(() => setStatus('idle'), 2000);
    }, 1000);
  };

  return (
    <DemoBox title="Variation A: Full Width Expand">
      <motion.button
        layout
        onClick={handleClick}
        className={`h-12 rounded-lg font-medium flex items-center justify-center overflow-hidden relative shadow-md transition-colors ${
          status === 'success' ? 'bg-green-500 text-white' : 'bg-violet-600 text-white'
        }`}
        animate={{ width: status === 'success' ? 280 : 140 }}
        transition={{ type: "spring", stiffness: 300, damping: 25 }}
      >
        <AnimatePresence mode="wait">
          {status === 'idle' && (
            <motion.span key="idle" initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} exit={{ y: -20, opacity: 0 }}>
              Submit
            </motion.span>
          )}
          {status === 'loading' && (
            <motion.span key="loading" initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} exit={{ y: -20, opacity: 0 }}>
              <Loader2 className="w-5 h-5 animate-spin" />
            </motion.span>
          )}
          {status === 'success' && (
            <motion.span key="success" initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} exit={{ y: -20, opacity: 0 }} className="flex items-center gap-2">
              <Check className="w-5 h-5" /> Successfully Saved
            </motion.span>
          )}
        </AnimatePresence>
      </motion.button>
    </DemoBox>
  );
};

// VARIATION B: Circular Morph
const ButtonTakeoverB = () => {
  const [status, setStatus] = useState('idle');

  const handleClick = () => {
    setStatus('loading');
    setTimeout(() => {
      setStatus('success');
      setTimeout(() => setStatus('idle'), 2000);
    }, 1000);
  };

  return (
    <DemoBox title="Variation B: Circular Morph">
      <motion.button
        layout
        onClick={handleClick}
        className={`rounded-full font-medium flex items-center justify-center overflow-hidden shadow-lg ${
           status === 'success' ? 'bg-green-500' : 'bg-slate-900 text-white'
        }`}
        animate={{ 
          width: status === 'idle' ? 140 : 50, // Shrink to circle
          height: 50,
          borderRadius: 9999
        }}
      >
        <AnimatePresence mode="wait">
          {status === 'idle' && (
            <motion.div key="idle" initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className="flex items-center gap-2">
              Send <Send className="w-4 h-4" />
            </motion.div>
          )}
          {status === 'loading' && (
             <motion.div key="loading" initial={{ scale: 0 }} animate={{ scale: 1 }} exit={{ scale: 0 }}>
               <Loader2 className="w-5 h-5 animate-spin text-white" />
             </motion.div>
          )}
          {status === 'success' && (
             <motion.div key="success" initial={{ scale: 0, rotate: -180 }} animate={{ scale: 1, rotate: 0 }} exit={{ scale: 0 }}>
               <Check className="w-6 h-6 text-white" />
             </motion.div>
          )}
        </AnimatePresence>
      </motion.button>
    </DemoBox>
  );
};

/* =========================================================================
   4. SPOTLIGHT DEMOS
   ========================================================================= */

// VARIATION A: Dim Background (The "Cinema" Mode)
const SpotlightDemoA = () => {
  const [focused, setFocused] = useState(false);
  return (
    <DemoBox title="Variation A: Dim Background">
      <div className="relative w-full max-w-xs p-4">
        {/* Dark Overlay */}
        <div 
          className={`absolute inset-0 bg-slate-900/10 backdrop-blur-[1px] rounded-xl transition-opacity duration-300 pointer-events-none z-10 ${focused ? 'opacity-100' : 'opacity-0'}`} 
        />

        <div className="space-y-4 relative z-0">
          <Input placeholder="Name" disabled={focused} />

          {/* Active Input moves to z-index 20 to sit ABOVE the dark overlay */}
          <div className={`relative transition-all duration-300 ${focused ? 'z-20 scale-105' : 'z-0'}`}>
            <div className="flex gap-2">
                <Input 
                    placeholder="Focus on me..." 
                    onFocus={() => setFocused(true)} 
                    onBlur={() => setFocused(false)} 
                    className="bg-white"
                />
                {focused && <Button size="icon" variant="secondary"><ArrowRight className="w-4 h-4"/></Button>}
            </div>
          </div>

          <Input placeholder="Email" disabled={focused} />
        </div>
      </div>
    </DemoBox>
  );
};

// VARIATION B: The "Glow Ring"
const SpotlightDemoB = () => {
    return (
      <DemoBox title="Variation B: Floating Glow Ring">
        <div className="w-full max-w-xs space-y-6 relative group">
            {/* In a real app, you'd calculate the position of this div 
               based on the active element. Here it's simulated with hover 
               using peer/group classes.
            */}
            <div className="relative">
                <Input placeholder="Hover me..." className="peer z-10 relative bg-transparent border-slate-300" />
                <div className="absolute -inset-1 bg-gradient-to-r from-violet-500 to-fuchsia-500 rounded-lg blur opacity-0 peer-focus:opacity-75 transition duration-500 z-0"></div>
            </div>

            <div className="relative">
                <Input placeholder="Or click me..." className="peer z-10 relative bg-transparent border-slate-300" />
                <div className="absolute -inset-1 bg-gradient-to-r from-cyan-500 to-blue-500 rounded-lg blur opacity-0 peer-focus:opacity-75 transition duration-500 z-0"></div>
            </div>
        </div>
      </DemoBox>
    );
  };

/* =========================================================================
   5. CURTAIN REVEAL DEMOS
   ========================================================================= */

// VARIATION A: Vertical Wipe (Classic)
const CurtainDemoA = () => {
  const [active, setActive] = useState(false);
  return (
    <DemoBox title="Variation A: Vertical Wipe">
      <div className="relative w-full h-48 bg-slate-100 rounded-lg overflow-hidden border">
        {/* Content Underneath */}
        <div className="absolute inset-0 flex items-center justify-center">
            <h1 className="text-2xl font-bold">DASHBOARD</h1>
        </div>

        {/* The Curtain */}
        <motion.div 
            className="absolute inset-0 bg-violet-600 flex items-center justify-center z-10"
            initial={{ y: 0 }}
            animate={{ y: active ? "-100%" : "0%" }}
            transition={{ duration: 0.8, ease: [0.22, 1, 0.36, 1] }}
        >
            <Button variant="secondary" onClick={() => setActive(true)}>Enter App</Button>
        </motion.div>

        {/* Reset Button */}
        {active && <button onClick={() => setActive(false)} className="absolute bottom-2 right-2 text-xs z-0">Reset</button>}
      </div>
    </DemoBox>
  );
};

// VARIATION B: Circular Iris Reveal
const CurtainDemoB = () => {
    const [active, setActive] = useState(false);
    return (
      <DemoBox title="Variation B: Iris Reveal">
        <div className="relative w-full h-48 bg-slate-900 rounded-lg overflow-hidden border text-white">
          {/* Content Underneath */}
          <div className="absolute inset-0 flex flex-col items-center justify-center">
              <h1 className="text-2xl font-bold text-violet-400">WELCOME</h1>
              <p className="text-xs">Access Granted</p>
          </div>

          {/* The Curtain (Clip Path) */}
          <motion.div 
              className="absolute inset-0 bg-white flex items-center justify-center z-10"
              initial={{ clipPath: "circle(150% at 50% 50%)" }}
              animate={{ clipPath: active ? "circle(0% at 50% 50%)" : "circle(150% at 50% 50%)" }}
              transition={{ duration: 0.8, ease: "easeInOut" }}
          >
              <Button className="bg-slate-900 text-white hover:bg-slate-800" onClick={() => setActive(true)}>Unlock</Button>
          </motion.div>

          {/* Reset Button */}
          {active && <button onClick={() => setActive(false)} className="absolute bottom-2 right-2 text-xs z-0 text-white/50">Reset</button>}
        </div>
      </DemoBox>
    );
  };

export default CreativeDemos;
