import React, { createContext, useContext, useState, useCallback } from 'react';

const NotificationContext = createContext();

export const useNotification = () => {
  const context = useContext(NotificationContext);
  if (!context) {
    throw new Error('useNotification must be used within a NotificationProvider');
  }
  return context;
};

export const NotificationProvider = ({ children }) => {
  const [activeNotification, setActiveNotification] = useState(null);

  const notify = useCallback((type, title, message) => {
    // type: 'success' | 'error' | 'info' | 'loading'
    const id = Date.now();
    setActiveNotification({ id, type, title, message });

    // Auto-dismiss after 3 seconds (unless it's a loading state)
    if (type !== 'loading') {
      setTimeout(() => {
        setActiveNotification((current) => (current?.id === id ? null : current));
      }, 3000);
    }
  }, []);

  const dismiss = useCallback(() => {
    setActiveNotification(null);
  }, []);

  return (
    <NotificationContext.Provider value={{ activeNotification, notify, dismiss }}>
      {children}
    </NotificationContext.Provider>
  );
};
